import logo from './logo.svg';
import './App.css';
import { useEffect, useState } from 'react';

function App() {
  const[item, setitem] = useState(["rock","paper", "knife"])
  const [playerone,setplayerone] = useState("")
  const [playertwo,setplayertwo] = useState("")
  const [win,setwin] = useState("")
  console.log(playerone)
  console.log(playertwo)

  const rand = function(){
      setplayerone(item[Math.floor(Math.random()*item.length)])

  }
  const randtwo = function(){
    setplayertwo(item[Math.floor(Math.random()*item.length)])



}

useEffect(function(){
  for(let i = 0; i<1;i++){
    if(playerone == "rock"&&playertwo == "knife"|| 
       playerone == "knife"&&playertwo == "paper"||
       playerone == "paper"&&playertwo == "rock"
       ){
        setwin("Թագավոր Էդգար")
    }

    if(playertwo == "rock"&&playerone == "knife"|| 
    playertwo == "knife"&&playerone == "paper"||
    playertwo == "paper"&&playerone == "rock"){
      setwin("Հպատակ Քրիստինե")
    }

    if(playerone == playertwo){
      setwin("draw")
    }
  }
},[playertwo])
  return (
    <div className="App">
      <button onClick={rand}>Թագավոր Էդգար</button>
      <div>{playerone}</div>
      <button onClick={randtwo}>Հպատակ Քրիստինե</button>
      <div>{playertwo}</div>
      <h1>{playerone&&playertwo!==""? win:""}</h1>
    </div>
  );
}

export default App;
